{!REQUIRESCRIPT("/soap/ajax/32.0/apex.js")}

var namespace_prefix_dot = ''; 
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'ApplicationUtilities' LIMIT 1"); 
var getname = gettingnamespace.getArray("records"); 
if(getname.length > 0 && getname[0].NamespacePrefix) 
{ 
namespace_prefix_dot = getname[0].NamespacePrefix+"."; 
}

// Do not include Namespace in this object
var params = { className: namespace_prefix_dot+"AttributeAssignmentRuleService", methodName: "createBatch", input: JSON.stringify({sObjectType: "Account" }), options: "{}" };

var result = sforce.apex.execute(namespace_prefix_dot+"VFActionFunctionController", "invokeServiceRun", params);

console.log(result);

if (result != null)
{
    alert('Batch Job Started');
}
else
{
    alert('Batch Job Failed');
}
